mapper.select:
SELECT U_IDX
FROM OTT_USER
WHERE U_ID = #{u_id}

select *
from ott_review
where u_idx = #{u_idx} and ott_idx = #{ott_idx}

mapper.update:
update ott_user
set is_warning = #{is_warning}
where u_idx = #{u_idx}

mapper.select:
SELECT * FROM (
	SELECT rownum r_num,a.* FROM (
		SELECT * FROM OTT_REVIEW
		WHERE ott_idx = #{ott_idx} and status = 1
		ORDER BY rv_idx DESC
	) a 
) WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE ott_review
SET status = 2
where rv_idx = #{rv_idx}

UPDATE OTT_REVIEW
SET CONTENT = #{content}
WHERE RV_IDX = #{rv_idx}

update ott_review
SET is_good = #{is_good}
where u_idx = #{u_idx} and ott_idx = #{ott_idx}

update ott_review
SET is_not = #{is_not}
where u_idx = #{u_idx} and ott_idx = #{ott_idx}

mapper.select:
select is_good
from ott_review
where u_idx = #{u_idx} and ott_idx = #{ott_idx}

select is_not
from ott_review
where u_idx = #{u_idx} and ott_idx = #{ott_idx}
