mapper.update:
update ott_user
SET u_pwd1 = #{u_pwd1}
where u_email = #{u_email}


UPDATE OTT_USER

SET
auth_key = #{auth_key}

WHERE u_id = #{u_id}

UPDATE OTT_USER
SET mail_check = 1, auth_status =1
WHERE u_email = #{u_email} AND auth_key = #{auth_key}

mapper.select:
SELECT * 
FROM OTT_USER
WHERE u_id = #{u_id} AND u_pwd1 = #{u_pwd1}

mapper.update:
UPDATE OTT_USER
SET

		oname=#{oname}
		, fname=#{fname}

	,

		u_name=#{u_name}

	,
	,
	,

		about_me=#{about_me}

WHERE u_idx=#{u_idx}

UPDATE OTT_USER
	SET u_pwd1 = #{u_pwd1}
WHERE u_idx = #{u_idx} AND u_id = #{u_id}
